import React from 'react';
import graduation from '../assets/graduation.png';
import flutter from '../assets/flutter.png';
import certificate from '../assets/';
import support from '../assets/support.png';

const features = [
  {
    img: graduation,
    title: 'Awesome Teachers',
    description: 'Professional coaches with a lot of teaching experience provide all of our training courses.',
  },
  {
    img: certificate,
    title: 'Global Certificate',
    description: 'All of our courses come with certificates and are globally accepted.',
  },
  {
    img: flutter,
    title: 'Best Programs',
    description: 'We build courses on the newest technologies. It gives students a sense of trust in learning.',
  },
  {
    img: support,
    title: 'Student Support Service',
    description: 'We are with you on the whole journey from enrolling to landing a job.',
  },
];

export default function Welcome() {
  return (
    <>
      <div className="mt-14 w-[415px] bg-prime2 mx-auto md:w-full md:h-[180px] md:mt-1">
        <div className="flex flex-col h-40 md:h-52 md:flex md:flex-col items-center md:w-[750px] mx-auto">
          <h1 className="font-semibold text-[30px] text-center text-org1 md:font-bold lg:text-[50px] md:text-[50px] md:w-[95%] md:px-20 lg:px-16 px-8">
            Welcome To FutureDev
          </h1>
          <div className="w-[405px] mx-3 md:hidden">
            <p className="items-center text-center text-white w-full px-7 text-[16px]">
              Our every course is designed in a way that they are easy to follow, latest in the market, provide you support for questions, and can help you land a job.
            </p>
          </div>
          <div className="hidden md:block w-full px-8">
            <p className="items-center text-center text-white w-full text-[15px] h-[52px] px-[90px] md:text-[18px]">
              Our every course is designed in a way that they are easy to follow, latest in the market, provide you support for questions, and can help you land a job.
            </p>
          </div>
        </div>
      </div>

      <div className="lg:w-full md:w-full lg:mx-auto md:mx-auto md:px-3 lg:mt-2">
        <div className="md:w-full md:flex md:flex-row md:mx-auto">
          <div className="flex flex-col w-[415px] mx-auto lg:w-full md:w-full md:flex lg:flex-row md:flex-row justify-evenly md:justify-around md:flex-wrap lg:mx-auto gap-3 md:gap-2">
            {features.map((feature, index) => (
              <ul
                key={index}
                className="flex flex-col justify-center h-[220px] items-center mt-6 mx-auto border-2 md:h-[265px] lg:w-[25%] rounded-md"
              >
                <div>
                  <img src={feature.img} alt={`${feature.title}-logo`} className="w-[80px] h-[80px] md:w-[100px] md:h-[100px]" />
                </div>
                <h2 className="font-semibold text-[20px] text-center text-org1 md:font-semibold md:text-[20px]">
                  {feature.title}
                </h2>
                <div className="pb-2">
                  <p className="text-[16px] px-4 md:text-[17px] text-white text-center">
                    {feature.description}
                  </p>
                </div>
              </ul>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
